"\n/* hop_survey->case->experiment->project->program */\nSELECT\n    node_hopsurvey.node_id as \"hop_survey.node_id\", node_hopsurvey.created as \"hop_survey.created\",  node_hopsurvey._props as \"hop_survey\",\n    node_case.node_id as \"case.node_id\", node_case._props as \"case\",\n    node_experiment.node_id as \"experiment.node_id\", node_experiment._props as \"experiment\",\n    node_project.node_id as \"project.node_id\", node_project._props as \"project\",\n    node_program.node_id as \"program.node_id\", node_program._props as \"program\"\nfrom node_hopsurvey\n    INNER JOIN edge_hopsurveydescribescase ON (edge_hopsurveydescribescase.src_id = node_hopsurvey.node_id)\n    INNER JOIN node_case ON (edge_hopsurveydescribescase.dst_id = node_case.node_id)\n    INNER JOIN edge_casememberofexperiment ON (edge_casememberofexperiment.src_id = node_case.node_id)\n    INNER JOIN node_experiment ON (edge_casememberofexperiment.dst_id = node_experiment.node_id)\n    INNER JOIN edge_experimentperformedforproject ON (edge_experimentperformedforproject.src_id = node_experiment.node_id)\n    INNER JOIN node_project ON (edge_experimentperformedforproject.dst_id = node_project.node_id)\n    INNER JOIN edge_projectmemberofprogram ON (edge_projectmemberofprogram.src_id = node_project.node_id)\n    INNER JOIN node_program ON (edge_projectmemberofprogram.dst_id = node_program.node_id)\nwhere node_hopsurvey.created > ? order by  node_hopsurvey.created ;"
